ATLAS = require("atlas")
COLOR = require("colors")
DIR = require("dir")
MAP = require("minimap")

t=0

Player = { x = 31, y = 4, dir = DIR.N }
view = { 1, 1, 1, 0, 1, 1, 1 }
IX = 1

function TIC()
	-- IX = ATLAS.test_mode(IX, view)
	input_handler()
	cls(0)
	local atlas = MAP.build_atlas(Player)
	ATLAS.draw_walls(atlas)
	ATLAS.debug_walls(atlas)
	MAP.draw(Player, 4, 14*8, 8)
	t=t+1
end

function handle_hovement()
	if btnp(0) then step(Player.dir) end
	if btnp(1) then step(DIR.opposite(Player.dir)) end
	if btnp(2) then Player.dir = DIR.rotate_ccw(Player.dir) end
	if btnp(3) then Player.dir = DIR.rotate_cw(Player.dir) end
end
input_handler = handle_hovement

function step(dir)
	local next_pos = DIR.step(Player, dir)
	if MAP.is_wall(next_pos) then return end
	Player.x = next_pos.x
	Player.y = next_pos.y
end

