t=0
x=96
y=24

COLOR = {
	BLACK = 0,
	PURPLE = 1,
	RED = 2,
	ORANGE = 3,
	YELLOW = 4,
	GREEN = 5,
	DARK_GREEN = 6,
	TURQOUISE = 7,
	NAVY = 8,
	DARK_BLUE = 9,
	BLUE = 10,
	CYAN = 11,
	WHITE = 12,
	LIGHT_GREY = 13,
	GREY = 14,
	DARK_GREY = 15
}

ATLAS = { 4, 4, 4, 3, 4, 4, 0 }
INFO = 
{
	{ width = 1, mx = 0, my = 0, offx = 00, len = 5 },
	{ width = 2, mx = 0, my = 17, offx = 01, len = 5 },
	{ width = 1, mx = 0, my = 17*2, offx = 03, len = 5 },
	{ width = 3, mx = 0, my = 17*3, offx = 04, len = 5 },
	{ width = 1, mx = 0, my = 17*4, offx = 07, len = 5 },
	{ width = 2, mx = 0, my = 17*5, offx = 08, len = 5 },
	{ width = 1, mx = 0, my = 17*6, offx = 10, len = 5 },
}

IX = 1
function TIC()

	if btnp(0) then ATLAS[IX]=ATLAS[IX]+1 end
	if btnp(1) then ATLAS[IX]=ATLAS[IX]-1 end
	if ATLAS[IX] < 1 then ATLAS[IX] = 0 end
	if btnp(2) then IX=IX-1 end
	if btnp(3) then IX=IX+1 end
	
	if IX <= 0 then IX = #ATLAS elseif IX > #ATLAS then IX = 1 end
	cls(1)
	for i=1,#ATLAS do
		if i == IX then
			print(ATLAS[i], 16 * i - 1, 8*16, COLOR.YELLOW)
		else
			print(ATLAS[i], 16 * i - 1, 8*16, COLOR.WHITE)
		end
	end
	draw_walls(ATLAS)
	t=t+1
end

function draw_walls(atlas)
	local ix = 1
	for ix=1,#atlas do
	-- Cell A
		cell = INFO[ix]
		map(cell.mx + atlas[ix] * cell.width, cell.my, cell.width, 14, cell.offx*8, 0, 0)
	end
end

